import Axios from 'axios';
import React, { useState } from 'react'
import styled from 'styled-components'

const SearchForm = styled.div`
display: flex;
align-items: center;
justify-content: center;
margin-top: 15px;
gap: 10px;
> button {
  border-radius: 10px;
  background-color: crimson;
  color: white;
  padding: 3px;
}
> button:active {
  background-color: white;
}
> button:hover {
  cursor: pointer;
}
> select {
  border-radius: 10px;
  padding: 3px;
}


`

function SearchFood({ setSearchResults }) {

const [searchType, setSearchType] = useState('');
const [searchPrice, setSearchPrice] = useState('');
const [searchRating, setSearchRating] = useState('');


const searchRestaurants = (e) => {
  e.preventDefault()
  Axios.get(`http://localhost:3001/search?type=${searchType}&price=${searchPrice}&rating=${searchRating}`)
  .then((response) => {
    setSearchResults(response.data);
  })
  .catch((error) => {
    console.error(error);
  });

}



  return (
    <SearchForm>
        <label>Type: </label>
        <select value={searchType} onChange={(e) => {
          setSearchType(e.target.value)
        }}><option></option> 
            <option>Other</option>
            <option>American</option>
            <option>BBQ</option>
            <option>Burgers</option>
            <option>Chicken</option>
            <option>Chinese</option>
            <option>Indian</option>
            <option>Jamaican</option>
            <option>Pizza</option>
            <option>Subs</option>
            <option>Tacos</option>
            <option>Thai</option>
        </select>
        <label>Price: </label>
        <select value={searchPrice} onChange={(e) => {
          setSearchPrice(e.target.value)
        }}><option></option> 
            <option>$</option>
            <option>$$</option>
            <option>$$$</option>
        </select>
        <label>Rating: </label>
        <select value={searchRating} onChange={(e) => {
          setSearchRating(e.target.value)
        }}><option></option> 
          <option>1</option>
            <option>2</option>
            <option>3</option>
            <option>4</option>
            <option>5</option>
            <option>6</option>
            <option>7</option>
            <option>8</option>
            <option>9</option>
            <option>10</option>
        </select>
        <button onClick={searchRestaurants}>Submit</button>
      </SearchForm>
  )
}

export default SearchFood
