import { Link } from 'react-router-dom'
import { useState } from 'react'
import styled from 'styled-components'
import logo from '../assets/foodLogo.png'
import menu from '../assets/menu.png'

const ListStyle = styled.ul`
list-style: none;
display: flex;
justify-content: flex-end;
gap: 30px;
padding: 0 30px;
`

const Logo = styled.img`
width: 150px;
height: 150px;
padding-top: 15px;
`

const NavBar = styled.nav`
display: flex;
justify-content: space-between;
align-items: center;
background-color: #FDF5DF;
width: 100vw;
padding: 10px;
height: 15vh;
font-size: 20px;
`

const Menu = styled.img`
@media (min-width: 600px) {
  display: none;
}
position: absolute;
top: 40px;


`
const RightSide = styled.div`
padding-right: 60px;
display: flex;
`

const LeftSide = styled.div`

`
const LinkDiv = styled.div`
display: flex;
gap: 20px;
padding-right: 20px;
// border: 1px solid black;
@media (max-width: 600px) {
  flex-direction: column;
 position: absolute;
 left: 0px;
 width: 100vw;
 text-align: center;
 top: ${(isOpen) => (isOpen ? '130px' : '-100px')};
 height: 150px;
 background-color: #FDF5DF;
}
`

const StyleLink = styled(Link)`
color: black;
text-decoration: none;
`

function Navbar() {

  const [isOpen, setIsOpen] = useState(false);


  const LinkDiv = styled.div`
  display: flex;
  gap: 20px;
  padding-right: 20px;
  border: 1px solid black;
  @media (max-width: 600px) {
  flex-direction: column;
   position: absolute;
   left: 0px;
   width: 100vw;
   text-align: center;
   top: ${() => (isOpen ? '130px' : '-1000px')};
   height: 190px;
   background-color: #FDF5DF;
   transition: 0.3s;
   box-shadow: 0 4px 2px -2px gray;
  }
  `




  return (
    <NavBar className='navBar'>
      <LeftSide>
        <Logo src={logo} alt='what to eat logo'/>
      </LeftSide>
      <RightSide>
        <Menu onClick={() => {setIsOpen(!isOpen)}} src={menu} alt='hamburger menu' />
        <LinkDiv>
          <StyleLink className='navLink' to='/'>Home</StyleLink>
          <StyleLink className='navLink'  to='/List'>List</StyleLink>
          <StyleLink className='navLink'  to='/Random'>Random</StyleLink>
        </LinkDiv>
      </RightSide>  
    </NavBar>
  )
}

export default Navbar
